sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
	initialize: function() {
	},

	//checks if the date is today
	isDateToday: function(strDate){

		// Create GlideDateTime objects for the current date and the passed in date
		var rightnow = new GlideDateTime().getLocalDate();
		var datePassed = new GlideDateTime(strDate).getLocalDate();

		if(rightnow.compareTo(datePassed) == 0){
			return true;
		}else{
			return false;
		}	

	},

	//added by Dave Jookhuu
	isDatePast: function(strDate){
		// Create GlideDateTime objects for the current date and the passed in date
		var rightnow = new GlideDateTime();
		var datePassed = new GlideDateTime(strDate);

		// If the testdate is before rightnow, return true, else return false
		if (datePassed.before(rightnow)) {
			return true;
		}
		else {
			return false;
		}
	},

	type: 'NeedItUtils'
};
